import './App.css';
import Animation from './animation';
import { useState } from 'react';
import parse from 'html-react-parser';


function App() {
  const [content, setContent] = useState('');

  function switchContent(e){
    let filename = `content${e.target.value}.json`;
    loadContent(filename);
  }
  async function loadContent(filename){
    fetch(filename).then(
      async response=>{
        let data = await response.json();
        console.log(data);
        setContent(parse(data.content));
      }
    );
  }
  let num = document.querySelector('#picker').value;
  let filename = `content${num}.json`;
  loadContent(filename);
  return (
    <>
      <header>
        <h1>Queue!!!!!!!!!!!!!!!</h1>
        <select id="picker" onChange={switchContent}>
          <option value="1">Content 1</option>
          <option value="2">Content 2</option>
        </select>
      </header>
      <main>
        {content}
      </main>
    </>
  );
}

export default App;
