//import './App.css';
import Animation from './animation';
import { ReactComponent as Logo } from './logo.svg';
import { useState, useEffect, useLayoutEffect, useCallback } from 'react';

function App() {

  const [animationName, setAnimationName] = useState('');
  const [anim] = useState(new Animation());
  const [filename, setFilename] = useState(null);

  function switchAnimation(e){
    let picked = `content${e.target.value}.json`;
    setFilename(picked); //trigger statechange
  }
  async function loadAnimation(filename){
    fetch(filename).then(
      async response=>{
        let data = await response.json();
        console.log(data);
        setAnimationName(data.name);
        anim.Prepare(data.animations, data.start, 'main');
      }
    );
  }
  useEffect(()=>{
    if (filename==null){
      let picker = document.querySelector('#picker').value;
      loadAnimation(picker);
    }else{
      loadAnimation(filename);
    }
  },[]);
  /* useCallback(()=>{
    fetch(filename).then(
      async response=>{
        let data = await response.json();
        console.log(data);
        setAnimationName(data.name);
        anim.Prepare(data.animations, data.start, 'main');
      }
    );
  },[filename, anim]); */

  return (
    <>
      <header>
        <label htmlFor="picker">Pick an animation</label>
        <select id="picker" onChange={switchAnimation}>
          <option value="1">Content 1</option>
          <option value="2">Content 2</option>
        </select>
      </header>
      <main>
        <h1>{animationName}</h1>
        <section id="animation-section">
          <h2 id="sub-text">Hello!!</h2>
          <Logo id="logo" />
          <div id="more-text">
            <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit. Integer elit metus, lacinia sed mattis id, aliquet ut nisl. Curabitur sit amet fringilla nulla. Mauris ut interdum augue. Suspendisse potenti. Fusce eget tellus a nisi lobortis rhoncus. In tincidunt malesuada consequat. Sed vel ex dignissim, facilisis arcu et, tempor neque. Duis iaculis felis vitae fermentum condimentum. Sed a enim sem. Donec urna massa, tincidunt in odio in, suscipit suscipit dolor. Praesent scelerisque odio vitae posuere accumsan. In vitae nisl sem.</p>
            <p>Fusce tellus dui, pretium ut ex in, dictum porta felis. Nullam tristique a massa sed tincidunt. Nunc condimentum elit et finibus sodales. Duis sed libero nisl. Donec finibus rhoncus lobortis. Nunc diam orci, vestibulum sed viverra in, maximus eu orci. Nulla rutrum nec lacus eu vulputate. Suspendisse non cursus nibh. Donec elementum hendrerit massa, ac venenatis purus luctus eu. Mauris mattis consequat risus, vel posuere felis. Nam non purus suscipit, pharetra dui non, lacinia massa. Vivamus dictum odio ut risus ultricies, sed viverra risus vehicula. </p>
          </div>
        </section>
      </main>
    </>
  );
}

export default App;
