		} else {
			return parseInt(time);
		}
	}
	stylestringToObj(styles, camelCase){
		let obj = {};
		if (typeof styles == 'string'){
			let rules = styles.split(/;\s?/);
			for(var r in rules){
				let rule = rules[r];
				if (rule){
					let parts = rule.split(/\s?:\s?/);
					let prop = camelCase ? parts[0].trim().replace(/-([a-z])/g, g => g[1].toUpperCase()) : parts[0].trim();
					if (parts.length === 2) obj[prop] = parts[1].trim();
				}
			}
			styles = obj;
		}
		return styles;
	}
