//import './App.css';
import Animation from './animation';
import { ReactComponent as Logo } from './logo.svg';
import { useState } from 'react';

function App() {
  const [animationName, setAnimationName] = useState('');

  function switchAnimation(e){
    let filename = `content${e.target.value}.json`;
    loadAnimation(filename);
  }
  async function loadAnimation(filename){
    fetch(filename).then(
      async response=>{
        let data = await response.json();
        console.log(data);
        setAnimationName(data.name);
      }
    );
  }
  let defaultFilename = 'content1.json';
  loadAnimation(defaultFilename);

  return (
    <>
      <header>
        <h1>Queue!!!!!!!!!!!!!!!</h1>
        <select id="picker" onChange={switchAnimation}>
          <option value="1">Content 1</option>
          <option value="2">Content 2</option>
        </select>
      </header>
      <main>
        <h2>{animationName}</h2>
        <Logo className="logo" />
      </main>
    </>
  );
}

export default App;
